import React from "react";

function Form({ inputText, setInputText }) {
    const inputTextHandler = (e) => {
        setInputText(e.target.value);
    }

    //? form içine yazdıklarımızı görmek için
    const submitTodoHandle = (e) => {
        e.preventDefault();
        /*
        //! deneme setInputText("Hasan")
        //! console.log(inputText);
        */
    }

    return (
        <form>
            <div className="search">
                <input value={inputText} type="text" className="todo-input" placeholder="Add..." onChange={inputTextHandler} />
                <button className="todo-button" type="submit" onClick={submitTodoHandle}>
                    <i className="fas fa-plus-circle"></i>
                </button>
            </div>

            <div className="select">
                <select name="todos" className="filter-todo">
                    <option value="all">All</option>
                    <option value="completed">Completed</option>
                    <option value="uncompleted">Uncompleted</option>
                </select>
            </div>
        </form>
    )
}

export default Form
